import React, { useState, useEffect, useRef } from "react";
import { MessageSquare, Smile, Send } from "lucide-react";
import EmojiPicker from "emoji-picker-react";
import "../styles/FamilyChat.css";


const initialMessages = [
    {
        from: "Maria (filha)",
        message: "Oi pai! Como está se sentindo hoje? Te amo! ❤️",
        time: "10:30",
    },
];

const FamilyChat = () => {
    const [messages, setMessages] = useState(initialMessages);
    const [newMessage, setNewMessage] = useState("");
    const [showEmojiPicker, setShowEmojiPicker] = useState(false);
    
    const endOfMessagesRef = useRef(null);
    const inputRef = useRef(null);
    
    useEffect(() => {
        endOfMessagesRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [messages]);
    
  const handleSendMessage = () => {
      if (!newMessage.trim()) return;

      const now = new Date();
      const formattedTime = now.toLocaleTimeString([], {
      hour: "2-digit",
      minute: "2-digit",
    });

    const newMsg = {
      from: "Você",
      message: newMessage,
      time: formattedTime,
    };
    
    setMessages([...messages, newMsg]);
    setNewMessage("");
    setShowEmojiPicker(false); 
};

const onEmojiClick = (emojiData) => {
    setNewMessage((prev) => prev + emojiData.emoji);
    inputRef.current.focus();
};
const emojiPickerRef = useRef(null);
useEffect(() => {
  function handleClickOutside(event) {
    if (
      emojiPickerRef.current &&
      !emojiPickerRef.current.contains(event.target)
    ) {
      setShowEmojiPicker(false);
    }
  }

  if (showEmojiPicker) {
    document.addEventListener("mousedown", handleClickOutside);
  }

  return () => {
    document.removeEventListener("mousedown", handleClickOutside);
  };
}, [showEmojiPicker]);


  return (
    <div className="family-chat-card">
      <div className="family-chat-header">
        <MessageSquare size={20} />
        <span>Mensagens da Família</span>
      </div>

      <div className="family-chat-body">
        {messages.map((msg, index) => (
          <div
            key={index}
            className={`family-message ${
              msg.from === "Você" ? "own-message" : ""
            }`}
          >
            <div className="message-header">
              <p className="from">{msg.from}</p>
            </div>
            <div className="message-content">
              <p>{msg.message}</p>
              <span className="time">{msg.time}</span>
            </div>
          </div>
        ))}
        <div ref={endOfMessagesRef} />
      </div>

      <div className="family-chat-input">
        <button
          className="emoji-button"
          title="Emoji"
          onClick={() => setShowEmojiPicker((prev) => !prev)}
        >
          <Smile size={20} />
        </button>

        {showEmojiPicker && (
          <div className="emoji-picker-container">
            <EmojiPicker onEmojiClick={onEmojiClick} />
          </div>
        )}

        <input
          type="text"
          placeholder="Escreva sua resposta..."
          ref={inputRef}
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && handleSendMessage()}
        />
        <button className="send-button" onClick={handleSendMessage}>
          <Send size={18} />
        </button>
      </div>
    </div>
  );
};

export default FamilyChat;
