import { useState } from "react";
import { DropletOff } from "lucide-react";
import styles from '../styles/ProfileCard.css';

function ProfileCard() {
  const [user, setUser] = useState(null);

  const loadUser = () => {
    const newUser = {
      nome: "Lucas Ramos Coelho",
      id: 123,
      idade: 80,
      estadoCivil: "Divorciado(a)",
      tipoSanguineo: "A-",
      alergia: "nenhum"
    };
    setUser(newUser);
  };

  const getAvatar = () => {
    if (!user?.nome) return "--";
    return user.nome
      .split(" ")
      .map((p) => p[0])
      .join("")
      .substring(0, 2)
      .toUpperCase();
  };

  return (
    <div style={styles.card}>
      <div style={styles.header}>
        <div style={styles.avatar}>{getAvatar()}</div>
        <div style={styles.info}>
          <strong>{user?.nome || "Nome"}</strong>
          <small>ID: #{user?.id || "--"}</small>
        </div>
      </div>

      <div style={styles.grid}>
        <div>
          <div style={styles.label}>Idade:</div>
          <div style={styles.value}>{user?.idade || "--"}</div>

          <div style={styles.label}>Estado civil:</div>
          <div style={styles.value}>{user?.estadoCivil || "--"}</div>
        </div>

        <div>
          <div style={styles.label}>Tipo sangu√≠neo:</div>
          <div style={styles.value}>{user?.tipoSanguineo || "--"}</div>

          <div style={styles.label}>Alergias:</div>
          <div style={styles.value}>
            <span style={styles.allergyTag}>
              {user?.alergia || "--"}
            </span>
          </div>
        </div>
      </div>

      <button style={styles.button} onClick={loadUser}>
        Carregar Dados
      </button>
    </div>
  );
}

export default ProfileCard;
