  import React, { useState } from 'react';
  import '../styles/CalendarioAgenda.css';
  import { CalendarDays, ChevronLeft, ChevronRight } from 'lucide-react';
  import {
    format,
    addMonths,
    subMonths,
    startOfMonth,
    endOfMonth,
    startOfWeek,
    endOfWeek,
    isSameDay,
    isSameMonth,
  } from 'date-fns';
  import { ptBR } from 'date-fns/locale';

  // Eventos falsos para testes de CSS
  const eventos = [
    { data: new Date(2025, 4, 2, 10, 0), descricao: 'Consulta com Dr. Cardoso', tipo: 'Médico' },
    { data: new Date(2025, 4, 5, 15, 0), descricao: 'Aniversário da Maria (neta)', tipo: 'Família' },
    { data: new Date(2025, 4, 10, 9, 30), descricao: 'Exame de sangue - Lab Central', tipo: 'Exame' },
    { data: new Date(2025, 4, 15, 13, 0), descricao: 'Reunião com fisioterapeuta', tipo: 'Médico' },
    { data: new Date(2025, 4, 18, 18, 0), descricao: 'Churrasco da família', tipo: 'Família' },
    { data: new Date(2025, 4, 20, 8, 0), descricao: 'Jejum para exames', tipo: 'Exame' },
    { data: new Date(2025, 4, 22, 11, 0), descricao: 'Vacina de reforço', tipo: 'Médico' },
    { data: new Date(2025, 4, 25, 16, 0), descricao: 'Visita dos filhos', tipo: 'Família' },
    { data: new Date(2025, 4, 28, 10, 0), descricao: 'Check-up anual', tipo: 'Médico' },
    { data: new Date(2025, 4, 30, 14, 0), descricao: 'Ultrassom abdominal', tipo: 'Exame' },
  ];

  const addDays = (date: Date, days: number) => {
    const result = new Date(date);
    result.setDate(result.getDate() + days);
    return result;
  };

  const CalendarioAgenda = () => {
    const [mesAtual, setMesAtual] = useState(new Date());
    const [diaSelecionado, setDiaSelecionado] = useState<Date | null>(null);

    const alterarMes = (direcao: 'anterior' | 'proximo') => {
      setMesAtual(direcao === 'anterior' ? subMonths(mesAtual, 1) : addMonths(mesAtual, 1));
      setDiaSelecionado(null);
    };

    const inicioMes = startOfMonth(mesAtual);
    const fimMes = endOfMonth(mesAtual);
    const inicioSemana = startOfWeek(inicioMes, { weekStartsOn: 0 });
    const fimSemana = endOfWeek(fimMes, { weekStartsOn: 0 });

    const dias = [];
    let dia = inicioSemana;
    while (dia <= fimSemana) {
      dias.push(dia);
      dia = addDays(dia, 1);
    }

    const eventosDoDia = (data: Date) =>
      eventos.filter((evento) => isSameDay(evento.data, data));

    return (
      <div className="calendario-container">
        <h2 className="calendario-titulo">
          <CalendarDays size={20} />
          Calendário e Agenda
        </h2>

        <div className="calendario-box">
          <div className="calendario-topo">
            <button onClick={() => alterarMes('anterior')}><ChevronLeft /></button>
            <h3>{format(mesAtual, 'MMMM yyyy', { locale: ptBR })}</h3>
            <button onClick={() => alterarMes('proximo')}><ChevronRight /></button>
          </div>

          <div className="calendario-grid">
            {['Dom', 'Seg', 'Ter', 'Qua', 'Qui', 'Sex', 'Sáb'].map((dia) => (
              <div key={dia}>{dia}</div>
            ))}
            {dias.map((data, index) => {
              const eventosHoje = eventosDoDia(data);
              return (
                <div
                  key={index}
                  className={`calendario-dia ${
                    !isSameMonth(data, mesAtual) ? 'fora-do-mes' : ''
                  } ${
                    isSameDay(data, diaSelecionado ?? new Date(0)) ? 'dia-selecionado' : ''
                  }`}
                  onClick={() => setDiaSelecionado(data)}
                >
                  {format(data, 'd')}
                  {eventosHoje.length > 0 && <span className="marcador-evento" />}
                </div>
              );
            })}
          </div>

          <div className="eventos-lista">
            <h4>Eventos do Dia</h4>
            {diaSelecionado ? (
              eventosDoDia(diaSelecionado).length > 0 ? (
                eventosDoDia(diaSelecionado).map((evento, index) => (
                  <div key={index} className={`evento-item tipo-${evento.tipo.toLowerCase()}`}>
                    <div className="evento-data">
                      <strong>{format(evento.data, 'dd/MM - HH:mm')}</strong>
                    </div>
                    <div className="evento-descricao">{evento.descricao}</div>
                    <span className="evento-tipo">{evento.tipo}</span>
                  </div>
                ))
              ) : (
                <p>Sem eventos para este dia.</p>
              )
            ) : (
              <p>Selecione um dia no calendário.</p>
            )}
          </div>
        </div>
      </div>
    );
  };

  export default CalendarioAgenda;
