import React from 'react';
import '../styles/CalendarioAgenda.css';
import { CalendarDays, ChevronLeft, ChevronRight } from 'lucide-react';

const eventos = [
  {
    data: '02/06 - 10:00',
    descricao: 'Consulta com Dr. Cardoso',
    tipo: 'Médico',
  },
  {
    data: '05/06 - 15:00',
    descricao: 'Aniversário da Maria (neta)',
    tipo: 'Família',
  },
  {
    data: '10/06 - 09:30',
    descricao: 'Exame de sangue - Laboratório Central',
    tipo: 'Exame',
  },
];

const CalendarioAgenda = () => {
  return (
    <div className="calendario-container">
      <h2 className="calendario-titulo">
        <CalendarDays size={20} />
        Calendário e Agenda
      </h2>

      <div className="calendario-box">
        <div className="calendario-topo">
          <button><ChevronLeft /></button>
          <h3>Maio 2025</h3>
          <button><ChevronRight /></button>
        </div>

        <div className="calendario-grid">
          <div>Dom</div><div>Seg</div><div>Ter</div><div>Qua</div><div>Qui</div><div>Sex</div><div>Sáb</div>
          {Array.from({ length: 35 }, (_, i) => (
            <div key={i} className="calendario-dia">{i < 5 ? '' : i - 4}</div>
          ))}
        </div>

        <div className="eventos-lista">
          <h4>Próximos Eventos</h4>
          {eventos.map((evento, index) => (
            <div key={index} className={`evento-item tipo-${evento.tipo.toLowerCase()}`}>
              <div className="evento-data"><strong>{evento.data}</strong></div>
              <div className="evento-descricao">{evento.descricao}</div>
              <span className="evento-tipo">{evento.tipo}</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default CalendarioAgenda;
