import React from "react";
import { MessageSquare } from "lucide-react";

type Message = {
  from: string;
  message: string;
  time: string;
};

const messages: Message[] = [
  {
    from: "Maria (filha)",
    message: "Oi pai! Como est√° se sentindo hoje? Te amo! ‚ù§Ô∏è",
    time: "10:30",
  },
  {
    from: "Jo√£o (neto)",
    message: "Vov√¥, estou com saudades! Vou te visitar no fim de semana! ü•∞",
    time: "09:15",
  },
];

const FamilyChat = () => {
  return (
    <div className="family-chat-card p-4 bg-white shadow-md rounded-lg border border-emerald-200">
      <div className="flex items-center gap-2 mb-4">
        <MessageSquare className="text-emerald-500" />
        <h2 className="font-semibold text-emerald-600">Mensagens da Fam√≠lia</h2>
      </div>

      <div className="space-y-3">
        {messages.map((msg, index) => (
          <div
            key={index}
            className="p-3 bg-emerald-50 rounded-md border border-emerald-100"
          >
            <p className="font-medium">{msg.from}</p>
            <p className="text-sm text-gray-700">{msg.message}</p>
            <p className="text-xs text-gray-400">{msg.time}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FamilyChat;
